import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdvancedSearch = () => {
  const [players, setPlayers] = useState([]); // State to store player data
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // State for search parameters
  const [searchParams, setSearchParams] = useState({
    team: '',
    name: '',
    season: '',
    position: ''
  });

  // State for pagination
  const [currentPage, setCurrentPage] = useState(1);
  const playersPerPage = 10;

  // Build the URL based on non-empty search parameters
  const buildUrl = () => {
    const baseUrl = 'http://localhost:8080/api/v1/nflplayer';
    const query = Object.entries(searchParams)
      .filter(([_, value]) => value) // Include only non-empty values
      .map(([key, value]) => `${key}=${encodeURIComponent(value)}`)
      .join('&');
    
    return query ? `${baseUrl}?${query}` : baseUrl;
  };

  // Fetch players based on search parameters
  const fetchPlayers = () => {
    setLoading(true);
    const url = buildUrl();

    axios.get(url)
      .then(response => setPlayers(response.data))
      .catch(error => setError(error.message))
      .finally(() => setLoading(false));
  };

  useEffect(() => {
    fetchPlayers();
  }, [searchParams]);

  // Handle input changes for search fields
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchParams(prevParams => ({ ...prevParams, [name]: value }));
  };

  // Handle page change
  const handlePageChange = (newPage) => setCurrentPage(newPage);

  // Calculate data for current page
  const indexOfLastPlayer = currentPage * playersPerPage;
  const indexOfFirstPlayer = indexOfLastPlayer - playersPerPage;
  const currentPlayers = players.slice(indexOfFirstPlayer, indexOfLastPlayer);

  return (
    <div>
      <h1>Advanced Search Page</h1>
      <p>Here you can conduct advanced searches for players, teams, and seasons.</p>
      
      {error && <p style={{ color: 'red' }}>{error}</p>}
      
      {/* Search Form */}
      <div>
        <input
          type="text"
          name="team"
          placeholder="Team"
          value={searchParams.team}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={searchParams.name}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="season"
          placeholder="Season"
          value={searchParams.season}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="position"
          placeholder="Position"
          value={searchParams.position}
          onChange={handleInputChange}
        />
        <button onClick={fetchPlayers}>Search</button>
      </div>

      {/* Loading Indicator */}
      {loading && <p>Loading...</p>}

      {/* Table for Displaying Players */}
      <table border="1">
        <thead>
          <tr>
            <th>Name</th>
            <th>Team</th>
            <th>Season</th>
            <th>Position</th>
            <th>Stats</th>
          </tr>
        </thead>
        <tbody>
          {currentPlayers.length > 0 ? (
            currentPlayers.map(player => (
              <tr key={player.id}>
                <td>{player.name}</td>
                <td>{player.team}</td>
                <td>{player.season}</td>
                <td>{player.position}</td>
                <td>{player.stats}</td> {/* Adjust fields as per your NFLPlayer structure */}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">No players found</td>
            </tr>
          )}
        </tbody>
      </table>

      {/* Pagination Controls */}
      <div>
        {Array.from({ length: Math.ceil(players.length / playersPerPage) }, (_, index) => (
          <button
            key={index + 1}
            onClick={() => handlePageChange(index + 1)}
            disabled={currentPage === index + 1}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

export default AdvancedSearch;
